mapper.select:
SELECT u.id,u.email,u.password,u.create_time as createTime,u.user_name AS userName, u.real_name AS realName,u.account_type AS accountType,u.mob_tel AS mobTel,u.fix_tel AS fixTel,u.qq,u.address,u.zip_code AS zipCode,u.introduction, GROUP_CONCAt(r.role) AS roleName, GROUP_CONCAT(r.id) AS roleNumbers, GROUP_CONCAT(r.remark) AS remark FROM cpsx_sys_dim_user u LEFT JOIN cpsx_sys_map_user_role ur on u.id=ur.user_id LEFT JOIN cpsx_sys_dim_role r on ur.role_id= r.id WHERE u.status=1
AND r.remark = #{queryParam.roleName}
GROUP BY(ur.user_id)

SELECT count(*)
FROM cpsx_sys_dim_user 
WHERE id=#{id}
AND password=#{psw}

SELECT u.user_id,a.remain FROM cpsx_sys_map_user_role u
LEFT JOIN cpsx_settle_dim_commission_apply a on u.user_id=a.user_id
WHERE u.role_id=3 
AND a.the_year=#{year} 
AND a.the_month=#{month}

mapper.update:
UPDATE cpsx_sys_dim_user SET email=#{email},
password=#{password},
user_name=#{userName}, user_type=#{userType}, real_name=#{realName}, account_type=#{accountType}, mob_tel=#{mobTel}, fix_tel=#{fixTel}, qq=#{qq}, zip_code=#{zipCode}, address=#{address} WHERE id=#{id}

UPDATE cpsx_sys_dim_user SET
password=#{password},
user_name=#{userName}, real_name=#{realName}, account_type=#{accountType}, mob_tel=#{mobTel}, fix_tel=#{fixTel}, qq=#{qq}, address=#{address}, zip_code=#{zipCode}, introduction=#{introduction} WHERE id=#{id}
